mapper.delete:
DELETE FROM packsack_prop
WHERE
packsack_prop.packsack_id = #{packId}
AND packsack_prop.prop_id =#{propId}

mapper.update:
UPDATE packsack_prop
SET packsack_prop.the_number = packsack_prop.the_number - 1
WHERE
    packsack_prop.packsack_id = #{packId}
AND packsack_prop.prop_id = #{propId}

UPDATE packsack_prop
SET packsack_prop.the_number = packsack_prop.the_number + 1
WHERE
    packsack_prop.packsack_id = #{packId}
AND packsack_prop.prop_id = #{propId}
